(function(a){a.widget("dsabs.basePopup",{options:{ajaxParameter:"ajax"},_create:function(){this._config={targetUrl:this._getTarget(),dataUrl:this._checkUrl(this.element.data("dataUrl"))}},_getTarget:function(){var b=document.createElement("a");if(this.element.attr("href")){b.href=this.element.attr("href")}if(this.element.attr("src")){b.href=this.element.attr("src")}if(b.href===""||b.href==="#"){console.error("No target found!")}return this._checkUrl(b)},_checkUrl:function(c){var b;if(typeof c==="undefined"){return}if(typeof c==="string"){b=document.createElement("a");b.href=c}else{b=c}if(b.search.search(this.options.ajaxParameter)===-1){if(b.search===""){b.search="?"+this.options.ajaxParameter+"=1"}else{b.search+="&"+this.options.ajaxParameter+"=1"}}return b.href}})}(jQuery));